import React, { useState, useEffect, useRef } from "react";
import "isomorphic-fetch";
import lottie from "lottie-web";
import animationData from "./assets/rocket.json";
import { Background, Title, Container, Mission, Explanation } from "./styles";

let animObj = null;
export default () => {
  const animBox = useRef(null);
  // const [isPaused, setIsPaused] = useState(false);
  // const [isPaused, setIsPaused] = useState(true);

  useEffect(() => {
    console.log(" component did mount ");
    animObj = lottie.loadAnimation({
      container: animBox.current, // the dom element that will contain the animation
      renderer: "svg",
      loop: true,
      autoplay: false,
      animationData: animationData, // the path to the animation json
    });
  }, []);

  return (
    <Background>
      <Container>
        <div style={{ width: 150, margin: "0 auto" }} ref={animBox}></div>

        <Title
          onMouseEnter={() => {
            lottie.unfreeze();
            animObj.play();
          }}
          onMouseLeave={() => lottie.freeze()}
        >
          NihonGoJS
        </Title>
        <Mission
          onMouseEnter={() => {
            lottie.unfreeze();
            animObj.play();
          }}
          onMouseLeave={() => lottie.freeze()}
        >
          ソフトウェア開発ブットキャンプ
        </Mission>
        <Explanation
          onMouseEnter={() => {
            lottie.unfreeze();
            animObj.play();
          }}
          onMouseLeave={() => lottie.freeze()}
        >
          ソフトウェア開発ブットキャンプ
        </Explanation>
      </Container>
    </Background>
  );
};
